exploreUrl:
@js:
sort = [];
push = (title, url, type) => sort.push({
 title: title,
 url: url,
 style: {
 layout_flexGrow: 1,
 layout_flexBasisPercent: type
 }
});
$ = (x) => `https://www.uaa.com/api/novel/app/novel/search?${x}&orderType=2&page={{page}}&searchType=1&size=40`;
push('All Categories', $(''), 1);
['Urban Passion', 'Campus Romance', 'Family Drama', 'System Powers', 'Time Travel', 'Eastern Fantasy'].map(p => {
 push(p, $(`category=${encodeURIComponent(p)}`), 0.25);
});
JSON.stringify(sort);

